import { Component, OnInit } from '@angular/core';
import { SharedService } from 'src/app/shared.service';
import { MdbModalService, MdbModalRef  } from 'mdb-angular-ui-kit/modal';
import { AddEditComponent } from '../add-edit/add-edit.component';

@Component({
  selector: 'app-show-del',
  templateUrl: './show-del.component.html',
  styleUrls: ['./show-del.component.css']
})
export class ShowDelComponent implements OnInit {
  modalRef: MdbModalRef<AddEditComponent> | null = null;
  constructor(private apiservice: SharedService, private modalService: MdbModalService) { 
  }

  deplist:any = [];
  dep:any;
  Modaltitle:string="";
  ngOnInit(): void {
    this.refreshDepList();
  }
  refreshDepList(){
    this.apiservice.GetDepartmentList().subscribe(
      dep =>{
        this.deplist = dep;
      }
    );
  }
  addClick(){
    this.dep = {
      departmentId:0,
      departmentName:""
    };
    this.Modaltitle = "Add Department";
    this.modalRef = this.modalService.open(AddEditComponent, {
      data: {
        title: this.Modaltitle,
        dep: this.dep
      }
    });
  }
}
<!-- Button trigger modal -->
<div class="container mt-3">
    <button type="button" class="btn btn-success alignright" title="add" data-toggle="modal" data-target="#exampleModal" data-backdrop="static" data-keyboard="false" (click)="addClick()">
        <i class="fa fa-plus" aria-hidden="true"></i>
      </button>
</div>
  <br /><br /><br />
<table class="table table-bordered table-dark">
    <thead>
        <tr>
            <th scope="col">Department ID</th>
            <th scope="col">Department Name</th>
            <th scope="col">Action</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let department of deplist">
            <td>{{department.departmentId}}</td>
            <td>{{department.departmentName}}</td>
            <td>
                <button type="button" class="btn btn-success" title="edit" data-toggle="modal" data-target="#exampleModal" (click)="editClick(department)">
                    <i class="fa-solid fa-pen-to-square"></i>
                </button>&nbsp;&nbsp;&nbsp;
                <button type="button" class="btn btn-danger" title="delete">
                    <i class="fa-solid fa-trash"></i>
                </button>
            </td>
        </tr>
    </tbody>
</table>

<!-- Modal -->
