import { Component, OnInit } from '@angular/core';
import { MdbModalRef } from 'mdb-angular-ui-kit/modal';

@Component({
  selector: 'app-add-edit',
  templateUrl: './add-edit.component.html',
  styleUrls: ['./add-edit.component.css']
})
export class AddEditComponent implements OnInit {
  public title: string = '';
  public dep: any;
  constructor(public modalRef: MdbModalRef<AddEditComponent>) {  }
  ngOnInit(): void {

  }

}
<div class="modal-header" style="background-color: rgb(130, 125, 125);">
    <h5 class="modal-title" id="exampleModalLabel">{{title}}</h5>
    <button
      type="button"
      class="btn-close"
      aria-label="Close"
      (click)="modalRef.close()"
    ></button>
  </div>
  <div class="modal-body">
    <form>
      <div class="form-group">
        <label for="exampleInputPassword1">Department Name</label>
        <input type="text" class="form-control" id="departmentName" value={{dep.departmentName}}>
      </div>
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-secondary" (click)="modalRef.close()">
      Close
    </button>
    <button type="button" class="btn btn-primary">Save changes</button>
  </div>
