import { Component, Inject, OnInit } from '@angular/core';
import { MdbModalRef } from 'mdb-angular-ui-kit/modal';
import { SharedService } from 'src/app/shared.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-add-edit',
  templateUrl: './add-edit.component.html',
  styleUrls: ['./add-edit.component.css']
})
export class AddEditComponent implements OnInit {
  public title: string = '';
  public dep: any;
  constructor(public modalRef: MdbModalRef<AddEditComponent>, private service: SharedService, private toastr: ToastrService, private refresh: ShowDelComponent) {  }
  ngOnInit(): void {

  }
  Save(){
    this.service.InsertDepartment(this.dep).subscribe(res =>{
      this.toastr.success(res);
      this.modalRef.close();
      this.refresh.refreshDepList();
    })
  }
  Update(){
    this.service.UpdateDepartment(this.dep).subscribe(res =>{
      alert(res);
    })
  }
}
