import { Component } from '@angular/core';
import { SharedService } from 'src/app/shared.service';
import { MdbModalRef, MdbModalService } from 'mdb-angular-ui-kit/modal';
import { ToastrService } from 'ngx-toastr';
import { NgxSpinnerService } from 'ngx-spinner';
import { AddEditComponent } from '../add-edit/add-edit.component';

@Component({
  selector: 'app-show-del-employee',
  templateUrl: './show-del.component.html',
  styleUrls: ['./show-del.component.css']
})
export class ShowDelEmployeeComponent {
  constructor(private apiService: SharedService, private modal: MdbModalService, private toast: ToastrService, private loader: NgxSpinnerService){

  }
  modalRef: MdbModalRef<AddEditComponent> | null = null
  emplist: any = [];
  emp: any;
  modalTitle: any = "";
  ngOnInit(){
    this.refreshEmpList();
  }
  refreshEmpList(){

      this.apiService.GetEmployeeList().subscribe(res =>{
        this.emplist = res;
      },
      error => {

      }
    )
  }
  addClick(){
    this.emp = {
      employeeId: 0,
      employeeName: "",
      departmentId: 0,
      departmentName: "",
      dOJ: new Date("01/01/1900 23:59"),
      profilePicture: ""
    };
    this.modalTitle = "Add Employee";
    this.modalRef = this.modal.open(AddEditComponent,{
      data: {
        emp: this.emp,
        modalTitle: this.modalTitle
      }
    });
    this.modalRef.onClose.subscribe(()=>{
      this.refreshEmpList();
    });
  }
  editClick(item: any){
    this.emp = item;
    this.modalTitle = "Edit Employee";
    this.modalRef = this.modal.open(AddEditComponent,{
      data: {
        emp: this.emp,
        modalTitle: this.modalTitle
      }
    });
    this.modalRef.onClose.subscribe(()=>{
      this.refreshEmpList();
    });
  }
  deleteClick(employeeId: any){
    this.loader.show();
    this.apiService.DeleteEmployee(employeeId).subscribe(res =>{
      this.toast.success(res, "Deleted Successfully");
      this.refreshEmpList();
      this.loader.hide();
    })
  }


}
<!-- Button trigger modal -->
<div class="container mt-3">
    <button type="button" class="btn btn-success alignright" title="add" data-toggle="modal" data-target="#exampleModal" data-backdrop="static" data-keyboard="false" (click)="addClick()">
        <i class="fa fa-plus" aria-hidden="true"></i>
      </button>
</div>
  <br /><br /><br />
<table class="table table-bordered table-dark">
    <thead>
        <tr>
            <th scope="col">Employee ID</th>
            <th scope="col">Employee Name</th>
            <th scope="col">Department Name</th>
            <th scope="col">Date of Joining</th>
            <th scope="col">Profile Picture</th>
            <th scope="col">Action</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let employee of emplist">
            <td>{{employee.employeeId}}</td>
            <td>{{employee.employeeName}}</td>
            <td>{{employee.departmentName}}</td>
            <td>{{employee.dOJ}}</td>
            <td>{{employee.profilePicture}}</td>
            <td>
                <button type="button" class="btn btn-success" title="edit" data-toggle="modal" data-target="#exampleModal" (click)="editClick(employee)">
                    <i class="fa-solid fa-pen-to-square"></i>
                </button>&nbsp;&nbsp;&nbsp;
                <button type="button" class="btn btn-danger" title="delete" (click)="deleteClick(employee.employeeId)">
                    <i class="fa-solid fa-trash"></i>
                </button>
            </td>
        </tr>
    </tbody>
</table>
